/*global require, process */

var control = require('../'),
    task = control.task;

task('mycluster', 'Host specific config for my cluster', function () {
    var options = {
            user: process.env.USER,
            scpOptions: ['-v']
        },
        addresses = {
            'localhost': {
                message: 'named'
            },
            '127.0.0.1': {
                message: 'numbered'
            }
        };
    return control.hosts(options, addresses); 
});

task('echo', 'Echo task to test host-specific options', function (host) {
    host.ssh('echo "This host is ' + host.message + '."');
});

control.begin();
